package com.orange.cas.service;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.orange.cas.dao.LogDao;import com.orange.cas.entity.LogEntity;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;@Service("casLogService")public class LogService extends ServicesManager<LogEntity, LogDao> 	implements Services<LogEntity> {	@Resource(name="casLogDao")    @Override    public void setDao(LogDao mDao) {        this.dao = mDao;    }		public LogEntity selectFirstOne(String arg0) {		return dao.selectFirstOne(arg0);	}}