package com.orange.cas.service;import com.orange.cas.dao.MemberDao;import com.orange.cas.entity.MemberEntity;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("casMemberService")public class MemberService extends ServicesManager<MemberEntity, MemberDao> implements		Services<MemberEntity> {    @Resource(name="casMemberDao")    @Override    public void setDao(MemberDao mDao) {        this.dao = mDao;    }        public int selectCount(String time){    	return dao.selectCount(time);    }        public int selectTotalCount(){    	return dao.selectTotalCount();    }	}