package com.orange.cas.service;import com.orange.cas.dao.PrivilegeDao;import com.orange.cas.entity.PrivilegeEntity;import com.orange.cas.entity.RoleEntity;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("casPrivilegeService")public class PrivilegeService extends ServicesManager<PrivilegeEntity, PrivilegeDao> implements		Services<PrivilegeEntity> {    @Resource(name="casPrivilegeDao")    @Override    public void setDao(PrivilegeDao mDao) {        this.dao = mDao;    }        /**	 * 根绝角色删除权限	 * 	 * @param role	 */	public void deleteByRole(RoleEntity role) {		PrivilegeEntity privilege = new PrivilegeEntity();		privilege.setRid(role.getId());		delete(privilege);	}    }