package com.orange.news.service;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;import com.orange.news.dao.ArticleDao;import com.orange.news.entity.ArticleEntity;import com.orange.news.entity.RaceEntity;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("newsArticleService")public class ArticleService extends ServicesManager<ArticleEntity, ArticleDao> implements		Services<ArticleEntity> {    @Resource(name="newsArticleDao")    @Override    public void setDao(ArticleDao mDao) {        this.dao = mDao;    }        public ArticleEntity selectPreBy(ArticleEntity art){    	return dao.selectPreBy(art);    }        public ArticleEntity selectNextBy(ArticleEntity art){    	return dao.selectNextBy(art);    }    }