package com.orange.news.service;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;import com.orange.news.dao.RaceDao;import com.orange.news.entity.RaceEntity;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("newsRaceService")public class RaceService extends ServicesManager<RaceEntity, RaceDao> implements		Services<RaceEntity> {    @Resource(name="newsRaceDao")    @Override    public void setDao(RaceDao mDao) {        this.dao = mDao;    }        public RaceEntity selectPreBy(RaceEntity race){    	return dao.selectPreBy(race);    }        public RaceEntity selectNextBy(RaceEntity race){    	return dao.selectNextBy(race);    }		}