package com.orange.race.service;import com.orange.core.base.dao.page.PagerModel;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;import com.orange.race.dao.RaceAddonManDao;import com.orange.race.entity.RaceAddonManEntity;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("raceAddonManService")public class RaceAddonManService extends ServicesManager<RaceAddonManEntity, RaceAddonManDao> implements		Services<RaceAddonManEntity> {    @Resource(name="raceAddonManDao")    @Override    public void setDao(RaceAddonManDao mDao) {        this.dao = mDao;    }        	public PagerModel selectListByRaceId(String raceId){    	return dao.selectListByRaceId(raceId);    }}