package com.orange.race.service;import com.orange.core.base.dao.page.PagerModel;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;import com.orange.race.dao.RaceAddonTeamDao;import com.orange.race.entity.RaceAddonTeamEntity;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("raceAddonTeamService")public class RaceAddonTeamService extends ServicesManager<RaceAddonTeamEntity, RaceAddonTeamDao> implements		Services<RaceAddonTeamEntity> {    @Resource(name="raceAddonTeamDao")    @Override    public void setDao(RaceAddonTeamDao mDao) {        this.dao = mDao;    }    	public PagerModel selectListByRaceId(String raceId){    	return dao.selectListByRaceId(raceId);    }    }