package com.orange.race.service;import com.orange.core.base.service.Services;import com.orange.core.base.service.ServicesManager;import com.orange.race.dao.RaceScoreDao;import com.orange.race.entity.RaceScoreEntity;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("raceScoreService")public class RaceScoreService extends ServicesManager<RaceScoreEntity, RaceScoreDao> implements		Services<RaceScoreEntity> {    @Resource(name="raceScoreDao")    @Override    public void setDao(RaceScoreDao mDao) {        this.dao = mDao;    }        public int updateCash(RaceScoreEntity e) {		return dao.updateCash(e);	}        public RaceScoreEntity selectScoreByMan(String raceId, String mobile, String cardId){    	return dao.selectScoreByMan(raceId, mobile, cardId);    }    }