mapper.select:
SELECT t.id, t.title, t.author, t.keyWord,
t.digest, t.bannerImg, t.sltImg, t.num, t.znum,
t.state, t.recommend, t.stick,
t.createTime, t.updateTime, t.publishTime, 
t.createAccount, t.updateAccount, t.publishAccount
FROM
t_news_race t
where 1=1

	and t.title like CONCAT('%','${title}','%' )


	and t.author like CONCAT('%','${author}','%' )


	and t.state =#{state}


	and t.recommend=#{recommend}


	and t.stick=#{stick}


	and t.type=#{type}

order by t.createTime desc, t.publishTime desc, t.state desc, t.orders
limit #{offset},#{pageSize}

select count(*)
FROM t_news_race t
where 1=1

	and t.title like CONCAT('%','${title}','%' )


	and t.author like CONCAT('%','${author}','%' )


	and t.state =#{state}


	and t.recommend=#{recommend}


	and t.stick=#{stick}


	and t.type=#{type}

SELECT t.id, t.title, t.author, t.digest, t.keyWord,t.state, t.sltImg FROM t_news_race t where 1=1
and t.state =#{state}
order by t.stick, t.recommend, t.publishTime desc limit 0,4

SELECT t.id, t.title, t.author, t.keyWord, t.digest, t.bannerImg, t.sltImg, t.num, t.znum, t.state, t.recommend, t.stick, t.createTime, t.updateTime, t.publishTime, t.createAccount, t.updateAccount, t.publishAccount FROM t_news_race t WHERE 1=1
and t.id=#{id}
and t.type=#{type}

SELECT t.id, t.title FROM t_news_race t where 1=1 and t.id > #{id}
and t.state =#{state}
order by t.id limit 1

SELECT t.id, t.title FROM t_news_race t where 1=1 and t.id < #{id}
and t.state =#{state}
order by t.id desc limit 1

mapper.insert:
insert into t_news_race (
id

	,title


	,author


	,content


	,state

,createtime

	,keyWord


	,digest


	,recommend


	,bannerImg


	,sltImg


	,stick


	,num


	,znum


	,favnum


	,cnum


	,orders


	,createAccount


	,type

) values(
id

	,#{title}


	,#{author}


	,#{content}


	,#{state}

,now()

	,#{keyWord}


	,#{digest}


	,#{recommend}


	,#{bannerImg}


	,#{sltImg}


	,#{stick}


	,#{num}


	,#{znum}


	,#{favnum}


	,#{cnum}


	,#{orders}


	,#{createAccount}


	,#{type}

)

mapper.update:
update t_news_race set id=#{id}

	,title=#{title}


	,author=#{author}


	,content=#{content}


	,state=#{state}


	,keyWord=#{keyWord}


	,digest=#{digest}


	,recommend=#{recommend}


	,bannerImg=#{bannerImg}


	,sltImg=#{sltImg}


	,stick=#{stick}


	,orders=#{orders}


	,num=#{num}


	,znum=#{znum}


	,favnum=#{favnum}


	,cnum=#{cnum}


	,updateAccount=#{updateAccount}
	,updateTime=now()


	,publishAccount=#{publishAccount}


	,publishTime=#{publishTime}


	,publishTime=now()


	,type=#{type}

where id=#{id}
